import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';
import { useParams } from 'react-router-dom'
import { IUsers } from '../models/IUsers';
import { UserService } from '../services/UsersService';

interface IParam {
    id: string
}

interface IState {
    loading: boolean,
    user: IUsers,
    erroMsg: string
}

const UserDetails: React.FC = () => {
    const { id } = useParams<IParam | any>();
    const [state, setState] = useState<IState>({
        loading: false,
        user: {} as IUsers,
        erroMsg: ''
    });

    useEffect(() => {
        if (id) {
            setState({ ...state, loading: true })
            UserService.getUserById(id)
                .then((res) => setState({
                    ...state, loading: false, user: res.data
                }))
                .catch((err) => setState({
                    ...state, loading: false, erroMsg: err.message
                }))
        }
    }, [id]);

    const {loading,user} = state;
    return (
        <>
            <div className="container">
                <h1>Single page details</h1>
                <h2>ID : {id}</h2>
                <h2>Name : {state.user.name}</h2>
                <h2>Username : {state.user.username}</h2>
                <h2>Email : {state.user.email}</h2>
                <div className='mt-4'>
                    <Link to={'/'} className='btn btn-info'>Go Back</Link>
                </div>
            </div>
        </>
    )
}

export default UserDetails
